import React, { useState, useEffect } from 'react';
import '../Card.css'
import axios from 'axios';

const Products = () => {
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState([]);

  useEffect(() => {
    setLoading(true);
    axios({
      method: 'GET',
      url: 'https://fakestoreapi.com/products',
    })
      .then(response => {
        console.log(response.data)
        setData(response.data)
      })
      .catch(error => console.log(error))
      .finally(() => setLoading(false))
  }, [])


  return (
    // <div className="products-container">
    //   {loading && (
    //     <div>
    //       {" "}
    //       <h1>Loading...</h1>
    //     </div>
    //   )}

    //   {data.map((product)=>(
    //     <div key={product.id} className="card">
    //       <div class="imgBx">
    //         <img src={product.image} alt="#"></img>
    //       </div>
    //       <div className="contentBx"> 
    //         <h6>{product.title}</h6>
    //         <h6>{`Price: ${product.price}`}</h6>
    //         <h6>{`Category: ${product.category}`}</h6>
    //       </div>
    //     </div>))}
    // </div>
    <div id="container">

      <div class="product-details">

        <h1>CHRISTMAS TREE</h1>
        <span class="hint-star star">
          <i class="fa fa-star" aria-hidden="true"></i>
          <i class="fa fa-star" aria-hidden="true"></i>
          <i class="fa fa-star" aria-hidden="true"></i>
          <i class="fa fa-star" aria-hidden="true"></i>
          <i class="fa fa-star-o" aria-hidden="true"></i>
        </span>

        <p class="information">" Let's spread the joy , here is Christmas , the most awaited day of the year.Christmas Tree is what one need the most. Here is the correct tree which will enhance your Christmas.</p>



        <div class="control">

          <button class="btn">
            <span class="price">$250</span>
            <span class="shopping-cart"><i class="fa fa-shopping-cart" aria-hidden="true"></i></span>
            <span class="buy">Get now</span>
          </button>

        </div>

      </div>

      <div class="product-image">

        <img src="https://images.unsplash.com/photo-1606830733744-0ad778449672?ixid=MXwxMjA3fDB8MHxzZWFyY2h8Mzl8fGNocmlzdG1hcyUyMHRyZWV8ZW58MHx8MHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60" alt=""></img>


          <div class="info">
            <h2> Description</h2>
            <ul>
              <li><strong>Height : </strong>5 Ft </li>
              <li><strong>Shade : </strong>Olive green</li>
              <li><strong>Decoration: </strong>balls and bells</li>
              <li><strong>Material: </strong>Eco-Friendly</li>

            </ul>
          </div>
        </div>
      </div>

  )
}

export default Products
